    protected function createOrUpdate(DocumentsSharingDocumentEvent $event, DocumentsSharingDocument $model)
    {
        $con = Propel::getConnection(DocumentsSharingDocumentTableMap::DATABASE_NAME);
        $con->beginTransaction();

        try {
            $model->setLocale($event->getLocale());

            if (null !== $id = $event->getId()) {
                $model->setId($id);
            }

            if (null !== $file = $event->getFile()) {
                $model->setFile($file);
            }

            if (null !== $fileKey = $event->getFileKey()) {
                $model->setFileKey($fileKey);
            }

            if (null !== $title = $event->getTitle()) {
                $model->setTitle($title);
            }

            if (null !== $description = $event->getDescription()) {
                $model->setDescription($description);
            }

            if (null !== $chapo = $event->getChapo()) {
                $model->setChapo($chapo);
            }

            if (null !== $postscriptum = $event->getPostscriptum()) {
                $model->setPostscriptum($postscriptum);
            }

            $model->save($con);

            $con->commit();
        } catch (\Exception $e) {
            $con->rollback();

            throw $e;
        }

        $event->setDocumentsSharingDocument($model);
    }

    protected function getDocumentsSharingDocument(DocumentsSharingDocumentEvent $event)
    {
        $model = DocumentsSharingDocumentQuery::create()->findPk($event->getId());

        if (null === $model) {
            throw new \RuntimeException(sprintf(
                "The 'documents_sharing_document' id '%d' doesn't exist",
                $event->getId()
            ));
        }

        return $model;
    }

