    public function check(DocumentsSharingDocumentEvent $event)
    {
        $documentId = $event->getId();
        $listShares = DocumentsSharingQuery::create()->orderByDateEndShare(Criteria::DESC)->filterByDocumentId("%".$documentId."%", Criteria::LIKE)->filterByDateEndShare(array('min' => 'now'))->find();
        $total = count($listShares);
        if(count($listShares) == 0){
            $event->getDispatcher()->dispatch(DocumentsSharingDocumentEvents::DELETE_FILE, $event);
            $event->getDispatcher()->dispatch(DocumentsSharingDocumentEvents::DELETE, $event);
        }
    }

    public function deleteFile(DocumentsSharingDocumentEvent $event)
    {
        $documentId = $event->getId();
        $file = DocumentsSharingDocumentQuery::create()->findPk($documentId);
        if(count($file)){
            $filename = basename($file->getFile());
            $folder = THELIA_WEB_DIR.'media'.DS.'documentssharing'.DS.'upload';
            $source_filepath = $folder.DS.$filename;
            unlink($source_filepath);
        }
    }

